async function getServerSideProps({ params, req }) {
    try {
        const protocol = req.headers["x-forwarded-proto"] || "http";
        const host = req.headers.host;
        const apiUrl = `${protocol}://${host}/api/exams/${params.id}`;
        // Forward the incoming request cookies to the API route so the session can be picked up.
        const res = await fetch(apiUrl, {
            headers: {
                cookie: req.headers.cookie || ""
            }
        });
        if (!res.ok) {
            console.error("API error response:", await res.text());
            return {
                notFound: true
            };
        }
        let exam = await res.json();
        if (!exam || !exam.questions) {
            return {
                notFound: true
            };
        }
        exam = JSON.parse(JSON.stringify(exam));
        return {
            props: {
                exam
            }
        };
    } catch (error) {
        console.error("Error fetching exam:", error);
        return {
            notFound: true
        };
    }
}
